var flagGaleriaDerecha = false;
var cantRotaciones = 0;
var myPixelDraw = {
	colorPicked: 0,
	cellColor: "#ecf0f1",
	defaultCells: 16,
	coloring: false,
	undo:[],
	undoColors:[],
	cantAcciones:0,
	fns:{
		calcSize: function(cant){
			if (cant==null) {
                cant = myPixelDraw.defaultCells;
            }
            var container = myPixelDraw.container;
            container.empty();
			var cantCeldas = cant*cant;
			
			for(var i=0;i<cantCeldas;i++)
				container.append('<div class="cell" draggable="true"></div>');
			$(".cell").css({
				'height':$("#container").height()/cant, 
				'width':$("#container").width()/cant,

			});
		},
		reSize: function(){
			
		},
		detectMouseUp: function(){
			$(document).on('mouseup', function(e){
				myPixelDraw.coloring = false;
				myPixelDraw.cantAcciones++;
			});
		},
		colorPalette: function(){
			$("#color-pick > *").each(function(index, e){
				$(e).css({'background-color':$(e).attr('class')});
			});
		},
		pickColor: function(){
			$("#color-pick div").on("click", function(){
				$(this).parent().find('.selected').removeClass("selected");
				myPixelDraw.colorPicked = $(this).attr('class');
				$(this).addClass("selected");
			});
		},
		colorIt: function(){
			$(document).on("mousedown", "#container .cell", function(e){
				e.preventDefault();
				console.log("el pinturillo");
				myPixelDraw.coloring = true;
				myPixelDraw.undo[myPixelDraw.undo.length]=[[$(e.target).index()]];
				myPixelDraw.undoColors[myPixelDraw.undoColors.length]=[[$(e.target).css('background-color')]];
				//$(this).css({'background-color':'red'});
				if(!$("#getColorTool").hasClass('gettingColor'))
				{
					myPixelDraw.colorPicked=$("#color").val();
					if(e.button == 2)
						$(this).css({'background-color':myPixelDraw.cellColor});
					else
						$(this).css({'background-color':myPixelDraw.colorPicked});
				}
				else
				{
					$.farbtastic("#colorpicker").setColor(rgb2hex($(e.target).css('background-color')));
					console.log("grabbed"+$(e.target).css('background-color'));
				}
			});
		},
		colorOnDrag: function(){
			$(document).on("mousemove", function(e){
				if(myPixelDraw.coloring&&!$("#getColorTool").hasClass('gettingColor'))
				{
					var x = e.clientX;
                    var y = e.clientY;
                    var celdaDrag = document.elementFromPoint(x, y);
                    console.log("Dragging");
                    
                    	var undoLength = myPixelDraw.undo.length;
                    	
                   		//myPixelDraw.undo[myPixelDraw.undo.length]=[[$(celdaDrag).index()],[$(celdaDrag).css('background-color')]];
					if(myPixelDraw.undo[undoLength-1][0].indexOf($(celdaDrag).index())===-1&&$(e.target).hasClass('cell')){
						myPixelDraw.undo[undoLength-1][0].push($(celdaDrag).index());
                    	myPixelDraw.undoColors[undoLength-1][0].push($(celdaDrag).css('background-color'));
					}

                    if ($(celdaDrag).hasClass('cell') && e.buttons != 2) {
                        $(celdaDrag).css('background-color', myPixelDraw.colorPicked);
                    } else if ($(celdaDrag).hasClass('cell') && e.buttons == 2) {
                    	debugger;
                        $(celdaDrag).css('background-color', myPixelDraw.cellColor);
                    }
				}
			});
		},
		reset: function(){
			console.log("maincrareset");
			$("#reset").on("click", function(){
				$(".cell").css({'background-color':myPixelDraw.cellColor});
			});
		},
		toggleBorders: function(){
			$("#toggle-border").on("mousedown", function(){
				console.log("remover bordes");
				$(".cell").toggleClass("no-borders");
			});
		},
		disableRightClick: function(){
			 myPixelDraw.container.on('contextmenu', function() {
                return false;
            });
		},
		grabImage: function(){
			$("#grab-it").on('click', function(e){
				console.log("capture");
				html2canvas(document.getElementById('container'), {
					onrendered: function(canvas){
						if(flagGaleriaDerecha==false)
						{
							$("#gallery-right").show(1000);
							flagGaleriaDerecha=true;
						}
						document.querySelector("#images-container").append(canvas);
						//document.body.appendChild(canvas);
					}
				});
			});
		},
		undo:function(){
			$("#undo").on('click', function(e){
				var lastUndo = myPixelDraw.undo.pop();
				var lastColorsUndo = myPixelDraw.undoColors.pop();
				console.log(lastUndo[0]);
				for(var i=0;i<lastUndo[0].length;i++)
				{
					$(".cell:nth-child("+(lastUndo[0][i]+1)+")").css({'background-color':(lastColorsUndo[0][i])});
				}
				//var lastCell = lastUndo.pop();
				
			});
			$(document).on('keydown', function(e)
			{
				if(e.which === 90 && e.ctrlKey)
				{
					var lastUndo = myPixelDraw.undo.pop();
					var lastColorsUndo = myPixelDraw.undoColors.pop();
					console.log(lastUndo[0]);
					for(var i=0;i<lastUndo[0].length;i++)
					{
						$(".cell:nth-child("+(lastUndo[0][i]+1)+")").css({'background-color':(lastColorsUndo[0][i])});
					}
				}
			});
		},
	},
	init:function(container){
		myPixelDraw.container = container;
        var fns = myPixelDraw.fns;
        for (var i = 0; i < Object.keys(fns).length; i++) {
            fns[Object.keys(fns)[i]]();
        }
	},
	
}
